class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vector<long long> d(n);
        long long res = 0;
        for(int i = 0; i < n; i ++) {
            d[i] = target[i] - nums[i]; 
        }
        for(int i = n - 1; i >= 0; i --) {
            if(i > 0)
                d[i] = d[i] - d[i - 1];
        }
        long long a = 0, b = 0;
        for(int i = 0; i < n; i ++) {
            if(d[i] > 0) a += d[i];
            else b -= d[i];
        }
        long long ans1 = max(a, b);
        return ans1;
    }
};
