#define ll long long
#define pb push_back
class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        vector<int> curr;
        ll ans=0;
        for(int i=0;i<nums.size();i++) {
            int p=nums[i]-target[i];
            if(curr.size()){
                if(curr.back()>0) {
                    if(p<0) {
                        ans+=curr.back();
                        curr.pb(p);
                    }
                    else if(p<curr.back()){
                        ans+=curr.back()-p;
                        curr.pb(p);
                    }
                    else{
                        curr.pb(p);
                    }
                }
                else{
                    if(p>0) {
                        ans+=abs(curr.back());
                        curr.pb(p);
                    }
                    else if(p>curr.back()){
                        ans+=abs(curr.back()-p);
                        curr.pb(p);
                    }
                    else{
                        curr.pb(p);
                    }
                }
            }
            else curr.pb(p);
        }
        ans+=abs(curr.back());
        return ans;

    }
};
