class Solution {
public:
using ll = long long;
    long long minimumOperations(vector<int>& a, vector<int>& b) {
        int n = a.size();

        for(int i =0;i<n;i++){
            a[i] = b[i] - a[i];
        }
        // for(auto i : a)cout<<i<<" ";
        // cout<<"\n";
        ll ans = abs(a[0]);

        for(int i = 1;i<n;i++){
            if(1LL*a[i]*a[i-1]>=0){
                if(abs(a[i])>abs(a[i-1])){
                    ans += abs(a[i] - a[i-1]);
                }
            }
            else ans += abs(a[i]);
        }
        return ans;
    }
};
