class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) 
    {
        int n=nums.size();
        long long ans=0;
        long long curdiff=0;
        for(int i=0; i<n; i++)
        {
            int diff= target[i]-nums[i];
            if(i==0 || diff==0 || (diff>0 && curdiff<=0) || (diff<0 && curdiff>=0))
                ans+= abs(diff);
            // else if(diff==0)
            //     curdiff=diff;
            else
            {
                if((diff>0)&&(curdiff<diff))
                    ans+=abs(diff-curdiff);
                else if((diff<0)&&(curdiff>diff))
                    ans+=abs(diff-curdiff);

            }


            curdiff= diff;
        }

        return ans;
    }
};
