class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        total = 0
        for i in range(len(nums)):
            nums[i] -= target[i]
        for i in range(len(nums)):
            if i == 0 or (nums[i] * nums[i - 1] <= 0):
                total += abs(nums[i])
            else:
                if nums[i]==nums[i-1]:
                    total+=0
                elif abs(nums[i])<abs(nums[i-1]):
                    total += 0
                else:
                    total+=abs(nums[i]-nums[i-1])
        return total
