class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
 int n = nums.size();
        vector<long long> ans(n);

        for (int i = 0; i < n; ++i) {
            ans[i] = target[i] - nums[i];
        }


        long long total_operations = 0;
        long long current_segment = 0;


        for (int i = 0; i < n; ++i) {
            if (i == 0 || (ans[i - 1] > 0 && ans[i] > 0) || (ans[i - 1] < 0 && ans[i] < 0)) {

                int additional_operations = abs(ans[i]) - abs(current_segment);
                if (additional_operations > 0) {
                    total_operations += additional_operations;
                }

                current_segment = ans[i];
            } else {

                total_operations += abs(ans[i]);

                current_segment = ans[i];
            }
        }

        return total_operations;
    }
};
