class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        res = 0
        currCost = abs(nums[0] - target[0])
        prev = nums[0] - target[0]
        status = 1
        for i in range(1, len(nums)):
            diff = nums[i] - target[i]
            # print(diff)
            if diff == 0:
                res += currCost
                prev = diff
                currCost = 0
                status = 1
            elif prev == 0:
                prev = diff
                currCost = abs(diff)
                status = 1
            elif (diff < 0 and prev > 0) or (diff > 0 and prev < 0):
                res += currCost
                prev = diff
                currCost = abs(diff)
                status = 1
            else:
                if status == 1:
                    # increase
                    if abs(diff) >= abs(prev):
                        currCost += abs(diff - prev)
                        prev = diff
                    else:
                        prev = diff
                        status = 0
                else:
                    if abs(diff) >= abs(prev):
                        res += currCost
                        currCost = abs(diff-prev)
                        prev = diff
                        status = 1
                    else:
                        prev = diff

            # print("end: ", res, currCost, prev, status)





            # print("end, ", curr)

        if currCost != 0:
            res += abs(currCost)

        return res
