class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n=nums.size();

        vector<int> d(n+10),a(n+10);
        for(int i=1;i<=n;i++){
            a[i]=target[i-1]-nums[i-1];
        }
        for(int i=1;i<=n;i++) d[i]=a[i]-a[i-1];
        long long p=0,q=0;
        for(int i=1;i<=n;i++)
        {
            if(d[i]>0) q+=d[i];
            else p-=d[i];
        }
        return max(p,q);
    }
};
