#include <vector>
#include <cstdlib>  // for std::abs

class Solution {
public:
    long long minimumOperations(std::vector<int>& nums, std::vector<int>& target) {
        // Calculate the difference array
        std::vector<long long> diff(nums.size());
        for (size_t i = 0; i < nums.size(); ++i) {
            diff[i] = static_cast<long long>(target[i]) - static_cast<long long>(nums[i]);
        }

        long long total_operations = 0;
        long long current_segment_diff = 0;

        for (size_t i = 0; i < diff.size(); ++i) {
            if (i == 0 || (diff[i - 1] > 0 && diff[i] > 0) || (diff[i - 1] < 0 && diff[i] < 0)) {
                long long additional_operations = std::abs(diff[i]) - std::abs(current_segment_diff);
                if (additional_operations > 0) {
                    total_operations += additional_operations;
                }
                current_segment_diff = diff[i];
            } else {
                total_operations += std::abs(diff[i]);
                current_segment_diff = diff[i];
            }
        }

        return total_operations;
    }
};
