class Solution {
    typedef long long ll;
    typedef vector<ll> vll;
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vll d(n);
        for(int i=0;i<n;i++){
            d[i]= nums[i]-target[i];
        }
        ll s = 1, m = 0; //1 => + , 0=> -
        ll ans = 0;
        // for(int i=0;i<n;i++){
        //     if(d[i]<0 && s==1){
        //         ans += m;
        //         m = 0;
        //         s = 0;
        //     }else if(d[i]>=0 && s==0){
        //         ans += m;
        //         m = 0;
        //         s = 1;
        //     }
        //     m = max(abs(d[i]),m);
        // }
        // ans+=m;
        for(int i=0;i<n;i++){
            if(i>0 && (d[i]*d[i-1]<=0)){
                m=0;
            }
            if(abs(d[i])>m){
                ans+= abs(d[i]) -m;
                m = abs(d[i]);
            }else{
                m = min(abs(d[i]),m);
            }
        }
        return ans;
    }
};
