class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        diff = [t - num for num, t in zip(nums, target)]
        #print(diff)

        answer = 0
        stack = []
        for i, d in enumerate(diff):
            if d <= 0:
                stack = []
                continue
            if not stack:
                answer += d
            elif stack[-1] < d:
                answer += d - stack[-1]

            while stack and stack[-1] > d:
                stack.pop()
            stack.append(d)
            #print(i, stack, answer)

        stack = []
        for i, d in enumerate(diff):
            if d >= 0:
                stack = []
                continue
            if not stack:
                answer += -d
            elif stack[-1] > d:
                answer += stack[-1] - d

            while stack and stack[-1] < d:
                stack.pop()
            stack.append(d)
            #print(i, stack, answer)

        return answer
