class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        vector<int>diff(nums.size(),0);
        for(int i=0;i<nums.size();i++){
            diff[i]= nums[i]-target[i];
        }
        long long cnt=0;
        long long op=0;
        long long curr=0;
        for(int i=0;i<nums.size();i++){
            if(i==0 || (diff[i-1]>0 && diff[i]>0) || (diff[i-1]<0 && diff[i]<0)){
                curr=abs(diff[i])- abs(op);
                if(curr>0){
                    cnt+=curr;
                }

            }
            else{
                cnt+= abs(diff[i]);
            }
            op=diff[i];
        }
        return cnt;
    }
};
