#define For(i, a, b) for (int i = (a); i < (b); i++)
#define vi vector<int>
#define pb push_back
#define sz(v) v.size()

class Solution
{
public:
    long long minimumOperations(vi &nums, vi &target)
    {
        if (nums == target)
            return 0;

        vi changes;
        int n = sz(nums);
        For(i, 0, n)
        {
            changes.pb(target[i] - nums[i]);
        }

        return calculateOperations(changes);
    }

private:
    long long calculateOperations(const vi &changes)
    {
        long long oper = 0;
        int curr = 0;
        int n = sz(changes);
        For(i, 0, n)
        {
            if (i == 0 || (changes[i - 1] > 0 && changes[i] > 0) || (changes[i - 1] < 0 && changes[i] < 0))
            {
                int ad = abs(changes[i]) - abs(curr);
                if (ad > 0)
                {
                    oper += ad;
                }
                curr = changes[i];
            }
            else
            {
                oper += abs(changes[i]);
                curr = changes[i];
            }
        }

        return oper;
    }
};
