#define ll long long
class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        ll n = nums.size();
        vector<ll> req(n);
        for(ll i=0;i<n;i++) {
            req[i] = target[i] - nums[i];
            // cout << req[i]<<" ";
        }
        // cout << endl;
        ll op = abs(req[0]);
        ll prev_add = 0,prevOp = op;
        if(req[0]<0) prev_add = 1;
        for(ll i=1;i<n;i++) {
            if(req[i]<0) {
                ll need = abs(req[i]);
                ll actual = need - prevOp;
                // cout<< i<<" "<<actual<<" "<<prev_add<<" "<<need <<endl;

                if(prev_add == 0 || prev_add == -1) {
                    prevOp = need;
                    prev_add = 1;
                    op += need;
                    continue;
                }
                prevOp = need;
                prev_add = 1;
                if(actual<0) continue;
                else {
                    op += actual;
                }
            } else if(req[i]>0){
                ll need = abs(req[i]);
                ll actual = need - prevOp;

                // cout<< i<<" "<<actual<<" "<<prev_add<<" "<<need <<endl;

                if(prev_add == 1 || prev_add == -1) {
                    prevOp = need;
                    prev_add = 0;
                    op += need;
                    continue;
                }
                prevOp = need;
                prev_add = 0;

                if(actual <= 0) continue;
                else {
                    op += actual;
                }
            } else {
                prevOp = 0;
                prev_add = -1;
            }
        }
        return op;
    }
};
