class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        //3 2 -4 1
        //4 2 -4 2
        //1 2 -1
        //2 -1 3
        //1 -3 4
        //转换为差分来思考，每一次选择两个端点，左端点+1，右端点后一个-1，或者左端点-1，右端点+1
        //1 0 2 1
        //4 -3 2 -1
        int n = nums.size();
        vector<int> a(n), b(n);
        a[0] = nums[0], b[0] = target[0];
        for(int i = 1; i < n; i++){
            a[i] = nums[i] - nums[i - 1];
            b[i] = target[i] - target[i - 1];
        }
        long long cnt1 = 0, cnt2 = 0, res = 0;
        for(int i = 0; i < n; i++){
            long long tmp = a[i] - b[i];
            if(tmp > 0){
                int tp = min(tmp, cnt1);
                tmp -= tp;
                cnt1 -= tp;
                res += tmp;
                cnt2 += tmp;
            }
            else{
                tmp = -tmp;
                int tp = min(tmp, cnt2);
                tmp -= tp;
                cnt2 -= tp;
                res += tmp;
                cnt1 += tmp;
            }
            // cout<<tmp<<endl;
        }
        // res += cnt1 + cnt2;
        return res;
    }
};
