impl Solution {
    pub fn minimum_operations(nums: Vec<i32>, target: Vec<i32>) -> i64 {
        let mut prev = 0;
        let mut diff = 0;
        let mut st = 0;
        let mut ans = 0;
        let n = nums.len();
        let mut pre = 0;
        let mut dif = 0;
        let mut sts = 0;
        let mut anss = 0;
        for i in 0..n {
            if prev == 0 {
                if nums[i] < target[i] {
                    prev = 1;
                    diff = target[i] - nums[i];
                    st += 1;
                } else if nums[i] > target[i] {
                    prev = -1;
                    diff = nums[i] - target[i];
                    st += 1;
                } else {
                    continue;
                }
            } else {
                if (nums[i] < target[i] && prev == 1 && diff == target[i] - nums[i]) ||
                   (nums[i] > target[i] && prev == -1 && diff == nums[i] - target[i]) {
                    st += 1;
                    continue;
                } else if nums[i] < target[i] && prev == 1 {
                    if target[i] - nums[i] > diff {
                        st = 1;
                        diff = target[i] - nums[i];
                    } else {
                        ans += (diff - target[i] + nums[i]) as i64;
                        diff = target[i] - nums[i];
                    }
                    continue;
                } else if nums[i] > target[i] && prev == -1 {
                    if nums[i] - target[i] > diff {
                        st = 1;
                        diff = nums[i] - target[i];
                    } else {
                        ans += (diff + target[i] - nums[i]) as i64;
                        diff = nums[i] - target[i];
                    }
                    continue;
                } else if nums[i] == target[i] {
                    ans += diff as i64;
                    prev = 0;
                    st = 0;
                    diff = 0;
                } else {
                    ans += diff as i64;
                    if nums[i] < target[i] {
                        prev = 1;
                        diff = target[i] - nums[i];
                        st += 1;
                    } else {
                        prev = -1;
                        diff = nums[i] - target[i];
                        st += 1;
                    }
                }
            }
        }
        let mut prevq = 0;
        let mut diffq = 0;
        let mut stq = 0;
        let mut ansq = 0;
        ans + diff as i64
    }
}
