class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
            long long n = nums.size();
        vector<  long long> vec(n+1);
        vector<  long long> dp(n+1);
        for(int i = 0;i<n;++i)
        {
            vec[i] = target[i]-nums[i];
        }
        dp[0] = abs(vec[0]);

        for(int i = 1;i<n;++i)
        {
            if(vec[i] == vec[i-1] || vec[i] == 0)
            {
                dp[i] =dp[i-1];
            }
            else if((vec[i]>=0 && vec[i-1]>=0)||(vec[i]<=0 && vec[i-1]<=0))
            {
                if(abs(vec[i]) > abs(vec[i-1]))
                {
                    dp[i] = dp[i-1]+abs(vec[i])-abs(vec[i-1]);
                }
                else
                {
                    dp[i] = dp[i-1];
                }
            }
            else
            {
                dp[i] = dp[i-1]+abs(vec[i]);
            }
        }
        return dp[n-1];
    }
};
