func minimumOperations(nums []int, target []int) int64 {
	n := len(nums)
	diff := make([]int64, n)
	for i := range diff {
		diff[i] = int64(target[i] - nums[i])
	}
	for i := n - 1; i >= 1; i-- {
		diff[i] -= diff[i-1]
	}
	var p, q int64
	for i := 1; i < n; i++ {
		if diff[i] < 0 {
			p -= diff[i]
		} else {
			q += diff[i]
		}
	}
	abs := func(x int64) int64 {
		if x < 0 {
			return -x
		}
		return x
	}
	ans := min(p, q) + abs(p-q)
	if p > q && diff[0] > 0 {
		diff[0] -= min(p-q, diff[0])
	} else if p < q && diff[0] < 0 {
		diff[0] += min(q-p, -diff[0])
	}
	return ans + abs(diff[0])
	// return ans
}
