class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vector<long long> diff(n);
        for (int i = 0; i < n; i++) {
            diff[i] = nums[i] - target[i];
        }
        long long cnt = diff[0];
        long long ans = abs(diff[0]);
        for (int i = 1; i < n; i++) {
            if (diff[i]*diff[i-1] > 0) {
                if (abs(cnt) >= abs(diff[i])) {
                    cnt = diff[i];
                }
                else {
                    ans += (abs(diff[i]) - abs(cnt));
                    cnt = diff[i];
                }
            }
            else {
                ans += abs(diff[i]);
                cnt = diff[i];
            }
        }
        return ans;
    }
};
