class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        diffs = [t - n for n, t in zip(nums, target)]

        total_moves = abs(diffs[0])
        current_sign = diffs[0] >= 0

        for diff in diffs[1:]:
            new_sign = diff >= 0
            if new_sign != current_sign:
                total_moves += abs(diff)
                current_sign = new_sign
            else:
                if abs(diff) > abs(diffs[0]):
                    total_moves += abs(diff) - abs(diffs[0])
            diffs[0] = diff

        return total_moves
