class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        for (int i = 0; i < nums.size(); i++) {
            target[i] = nums[i] - target[i];
        }
        int currDiff = 0;
        long long res = 0;
        for (int i = 0; i < nums.size(); i++) {
            if (target[i] == 0) {
                currDiff = 0;
                continue;
            }
            if (target[i] < 0) {
                int diff = target[i];
                target[i] = 0;
                if (diff < currDiff && currDiff < 0) {
                    res += abs(diff - currDiff);
                    currDiff = diff;
                } else if (diff > currDiff && currDiff < 0) {
                    currDiff = diff;
                } else if (currDiff >= 0) {
                    res += abs(diff);
                    currDiff = diff;
                }
            }
            if (target[i] > 0) {
                int diff = target[i];
                target[i] = 0;
                if (diff > currDiff && currDiff > 0) {
                    res += abs(diff - currDiff);
                    currDiff = diff;
                } else if (diff < currDiff && currDiff > 0) {
                    currDiff = diff;
                } else if (currDiff <= 0) {
                    res += abs(diff);
                    currDiff = diff;
                }
            }
        }
        return res;
    }
};
