class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        pre = 0
        r = 0
        for a, b in zip(nums, target):
            diff = a - b
            if diff >= 0 and pre >= 0:
                r += max(0, diff - pre)
                pre = diff
            elif diff < 0 and pre < 0:
                r += max(0, pre - diff)
                pre = diff
            else:
                r += abs(diff)
                pre = diff
        return r
