/**
 * @param {number[]} nums
 * @param {number[]} target
 * @return {number}
 */
var minimumOperations = function(nums, target) {
    const n = nums.length;
    const diff_arr = new Array(n);

    let ans = 0;
    for (let i = 0; i < n; ++i) {
        diff_arr[i] = target[i] - nums[i];
    }

    let curr_seg_dff = 0;

    for (let i = 0; i < n; ++i) {
        if ((diff_arr[i - 1] > 0 && diff_arr[i] > 0) || i === 0 || (diff_arr[i - 1] < 0 && diff_arr[i] < 0)) {
            if (Math.abs(diff_arr[i]) - Math.abs(curr_seg_dff) > 0) {
                ans += Math.abs(diff_arr[i]) - Math.abs(curr_seg_dff);
            }
            curr_seg_dff = diff_arr[i];
        } else {
            ans += Math.abs(diff_arr[i]);
            curr_seg_dff = diff_arr[i];
        }
    }

    return ans;
};
