class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        long long sub[n], diff[n];
        for(int i = 0; i < n; i++) sub[i] = nums[i]-target[i];
        diff[0] = sub[0];
        for(int i = 0; i < n - 1; i++){
            diff[i+1] = sub[i+1] - sub[i];
        }
        // for(auto x:diff) cout << x << " ";
        // cout << endl;
        long long ans = abs(diff[0]), ch = diff[0];
        // cout << diff[0] << " " << ch << endl;
        for(int i = 1; i < n; i++){
            if(diff[i] == 0) continue;
            int t = ch + diff[i];
            if((diff[i] > 0) && (ch < 0)){
                diff[i] += max(-diff[i], ch);
            } else if((diff[i] < 0) && (ch > 0)){
                diff[i] += min(-diff[i], ch);
                // cout << i << endl;
            }
            cout << diff[i] << " " << t << endl;
            ans += abs(diff[i]);
            ch = t;
        }
        // ans += abs(ch);
        return ans;
    }
};
