class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        for (int i = 0; i < nums.size(); ++i)
            nums[i] -= target[i];
        long long ops_req = 0;
        int last = 0;
        for (int i = 0; i < nums.size(); ++i) {
            if (nums[i] == last) {
                continue; //no op needed
            }
            if (nums[i] > 0) {
                if (last < 0) {
                    ops_req += nums[i];
                } else {
                    if (last < nums[i]) {
                        ops_req += nums[i] - last;
                    }
                }
            }
            if (nums[i] < 0) {
                if (last > 0) {
                    ops_req += -nums[i];
                } else {
                    if (last > nums[i]) {
                        ops_req += last - nums[i];
                    }
                }
            }
                        last = nums[i];
        }
        return ops_req;
    }
};
