class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& t) {
        vector<int> v;
        int n = nums.size();
        for (int i = 0; i < n; i++) {
            int x = t[i] - nums[i];
            v.push_back(x);
        }
        vector<int> temp;
        long long ans = 0;
        for (int i = 0; i < v.size(); i++) {
            if (v[i] == 0)
                continue;
            if (v[i] > 0) {
                int j = i;
                while (j < n && v[j] >= 0) {
                    temp.push_back(v[j]);
                    j++;
                }
                long long cur = 0;
                if (temp.size()) {
                    cur = temp[0];
                    for (int k = 1; k < temp.size(); k++) {
                        int y=0; int z=temp[k] - temp[k - 1];
                        y=max(y,z);
                        cur += y;
                    }
                }
                ans += cur;
                temp.clear();
                i = j;
                i--;
            } else {
                if (v[i] < 0) {
                    int j = i;
                    while (j < n && v[j] <= 0) {
                        temp.push_back(-v[j]);
                        j++;
                    }
                    long long cur = 0;
                    if (temp.size()) {
                        cur = temp[0];
                        for (int k = 1; k < temp.size(); k++) {
                           int y=0; int z=temp[k] - temp[k - 1];
                        y=max(y,z);
                        cur += y;
                        }
                    }
                    ans += cur;
                    temp.clear();
                    i = j;
                    i--;
                }
            }
        }
            return ans;
        }
    };
