#define ll long long

class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        ll n = nums.size();
        vector<ll> changes(n);
        ll op = 0;
        for(int i = 0; i < n; i++) {
            changes[i] = target[i] - nums[i];
        }
        ll curr_d = 0;
        for(int i = 0; i < n; i++) {
            if(i == 0 || (changes[i]*changes[i-1] > 0)) {
                ll extra = abs(changes[i]) - abs(curr_d);
                if(extra > 0) op += extra;
            }
            else {
                op +=  abs(changes[i]);
            }
            curr_d = changes[i];
        }
        return op;
    }
};
