class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vector<long long> v = vector<long long>(n, 0);
        v[0] = target[0] - nums[0];
        for (int i = 1; i < n; i++) {
            v[i] = (long long)target[i] - (long long)nums[i] - (long long)target[i-1] + (long long)nums[i-1];
        }

        /*
        for (int x: v)
            cout << x << ' ';
        cout << '\n';
        */

        int pos_idx = 0, neg_idx = 0;
        long long cnt = 0;
        while (pos_idx < n || neg_idx < n) {
            while (pos_idx < n && v[pos_idx] <= 0) {
                pos_idx++;
            }
            while (neg_idx < n && v[neg_idx] >= 0) {
                neg_idx++;
            }

            //cout << pos_idx << ' ' << neg_idx << endl;

            long long dec = 0;
            if (pos_idx < n && neg_idx < n) {
                dec = min(v[pos_idx], -v[neg_idx]);
            }
            else if (pos_idx < n) {
                dec = v[pos_idx];
            }
            else if (neg_idx < n) {
                dec = -v[neg_idx];
            }
            if (pos_idx < n) v[pos_idx] -= dec;
            if (neg_idx < n) v[neg_idx] += dec;
            cnt += dec;
        }

        return cnt;
    }
};
