class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        diffs = [a - b for a, b in zip(nums, target)]
        ans = 0
        neg = False
        prev = 0

        for d in diffs:
            if d == 0:
                prev = 0
                continue
            elif d > 0:
                if neg:
                    ans += d
                    neg = False
                else:
                    ans += max(0, d - prev)
                prev = d
            else:
                d = -d
                if neg:
                    ans += max(0, d - prev)
                else:
                    ans += d
                    neg = True
                prev = d

        return ans
