class Solution:
    def minimumOperations(self, a: List[int], b: List[int]) -> int:

        A = []
        n = len(a)

        for i in range(n):
            s = a[i] - b[i]
            if not A:
                A.append([s])
                continue
            elif A[-1][-1] <= 0 and s <= 0 or A[-1][-1] > 0 and s > 0:
                A[-1].append(s)
            else:
                A.append([s])


        # print(A)

        def f(A):
            for i in range(len(A)): A[i] = abs(A[i])

            ans = A[0]
            done = A[0]

            for i in range(1, len(A)):
                if A[i] > done:
                    ans += A[i] - done

                done = A[i]

            return ans


        ans = 0
        for v in A:
            ans +=f(v)
        return ans
