class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vector<long long> diff(n);
        for (int i = 0; i < n; i++) {
            diff[i] = target[i] - nums[i];
        }

        long long total = 0;
        long long curr = 0;

        for (int i = 0; i < n; i++) {
            if (i == 0 or (diff[i] < 0 and diff[i - 1] < 0) or (diff[i] > 0 && diff[i-1] > 0)) {
                if (abs(diff[i]) - abs(curr) > 0) {
                    total += abs(diff[i]) - abs(curr);
                }
            } else {
                total += abs(diff[i]);
            }
            curr = diff[i];
        }

        return total;
    }
};
