class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ans=0;
        for(int i=0;i<nums.size();i++){
            nums[i]-=target[i];
        }
        for(int i=0;i<nums.size();i++){
            if(i&&nums[i]==nums[i-1])continue;
            else {
                int d=i?-nums[i-1]:0;
                if(1ll*(nums[i]+d)*nums[i]<=0)continue;
                else ans+=min(abs(nums[i]+d),abs(nums[i]));
            }
        }
        return ans;
    }
};
