class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int upcha[100010],downcha[100010];
        for(int i=0;i<nums.size();i++){
            int temp=nums[i]-target[i];
            if(temp>0){
                upcha[i]=temp;
                downcha[i]=0;
            }
            else{
                upcha[i]=0;
                downcha[i]=-temp;
            }
            //cout<<upcha[i]<<' '<<downcha[i]<<endl;
        }
        long long flag=0,ans=0;
        for(int i=0;i<nums.size();i++){
            if(upcha[i]>flag){
                ans+=upcha[i]-flag;
            }
            flag=upcha[i];
        }
        //cout<<ans<<endl;
        flag=0;
        for(int i=0;i<nums.size();i++){
            if(downcha[i]>flag){
                ans+=downcha[i]-flag;
            }
            flag=downcha[i];
        }
        //cout<<ans<<endl;
        //cout<<"_______________"<<endl;
        return ans;
    }
};
