typedef long long ll;
class Solution {
public:
    ll minimumOperations(vector<int> &hnums,vector<int> &htar){
        ll hresans=0,hcurr=0;
        vector<int> hdiff;
        for(int i=0;i<hnums.size();i++){
            hdiff.push_back(hnums[i]-htar[i]);
        }
        for(int i=0;i<hnums.size();i++){
            if(i==0 or (hdiff[i-1]>0 and hdiff[i]>0) or (hdiff[i-1]<0 and hdiff[i]<0)){
                int hadd=abs(hdiff[i])-abs(hcurr);
                if(hadd>0){
                    hresans+=hadd;
                }
            }
            else{
                hresans+=abs(hdiff[i]);
            }
            hcurr=hdiff[i];
        }
        return hresans;
    }
};
