class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        vector<int>arr;
        for(int i=0; i<nums.size(); i++){
            arr.push_back(target[i]-nums[i]);
        }

        // vector<set<int>>v;
        long long int ans = 0;

        int i=0;
        while(i<arr.size()){
            vector<int>temp;
            int j=i;
            while(j<arr.size()){
                if((arr[j]<0 && arr[i]<0) || (arr[j]>0 && arr[i]>0)){
                    temp.push_back(abs(arr[j]));
                }
                else{
                    break;
                }
                j++;
            }
            while(j<arr.size()){
                if(arr[j] == 0){
                    j++;
                }
                else{
                    break;
                }
            }
            i=j;
            // v.push_back(temp);

            // if(*(temp.begin()) >0){
            //     ans = ans + 1ll*(*(temp.rbegin()));
            // }
            // else{
            //     ans = ans + 1ll*(abs(*(temp.begin())));
            // }

            int mini= 0;
            int maxi = 0;
            for(int k=0; k<temp.size(); k++){
                if(maxi>temp[k]){
                    ans = ans + maxi - mini;
                    mini = temp[k];
                    maxi = temp[k];
                }
                else{
                    maxi = temp[k];
                }
            }
            ans = ans + maxi - mini;

            temp.clear();
        }



        // for(int j =0; j<v.size(); j++){
        //     if(*(v[j].begin()) >0){
        //         ans = ans + 1ll*(*(v[j].rbegin()));
        //     }
        //     else{
        //         ans = ans + 1ll*(abs(*(v[j].begin())));
        //     }
        // }
        return ans;
    }
};
