#define ll long long
class Solution {
public:
    long long minimumOperations(vector<int>& a, vector<int>& b) {
        int n = a.size();
        vector<ll> f(n , 2e9);
        f[0] = abs(a[0] - b[0]);
        for(int i=1; i<n; i++) {
            ll t1 = a[i] - b[i] , t2 = a[i - 1] - b[i - 1];
            if(t1 * t2 > 0) {
                t1 = abs(t1) , t2 = abs(t2);
                if(t1 <= t2) f[i] = f[i - 1];
                else f[i] = f[i - 1] + t1 - t2;
            } else {
                f[i] = f[i - 1] + abs( t1 ) ;
            }
        }
        return f[n - 1];
    }
};
