#define vi vector<int>
#define ll long long
class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vi a(n);
        for(int i=0; i<n; i++) a[i] = nums[i] - target[i];

        ll ans = 0;
        int i = 0;
        while(i < n){
            if(a[i] >= 0){
                ans += a[i];
                i++;
                while(i < n && a[i] >= 0){
                    ans += max(a[i] - a[i-1], 0);
                    i++;
                }
            }else{
                ans += abs(a[i]);
                i++;
                while(i < n && a[i] < 0){
                    ans += max(abs(a[i]) - abs(a[i-1]), 0);
                    i++;
                }
            }
        }
        return ans;
    }
};
