func minimumOperations(nums []int, target []int) int64 {
	if len(nums) == 0 {
		return 0
	}
	l := len(nums)
	tmp := make([]int, l)
	for i := 0; i < l; i++ {
		tmp[i] = nums[i] - target[i]
	}
	return minimum(tmp)
}

func minimum(tmp []int) int64 {
	var ans int64
	left := 0
	right := left + 1
	for right < len(tmp) {
		if sameOp(tmp[left], tmp[right]) {
			right++
		} else {
			ans += cal(tmp[left:right])
			left = right
			right = right + 1
		}
	}
	ans += cal(tmp[left:right])
	return ans
}

func cal(arr []int) int64 {
	var ans int64
	if len(arr) == 0 {
		return ans
	}
	if arr[0] < 0 {
		arr[0] = -arr[0]
	}
	dp := make([]int64, len(arr))
	dp[0] = int64(arr[0])
	for i := 1; i < len(arr); i++ {
		if arr[i] < 0 {
			arr[i] = -arr[i]
		}
		if arr[i] <= arr[i-1] {
			dp[i] = dp[i-1]
		} else {
			dp[i] = dp[i-1] + int64(arr[i]) - int64(arr[i-1])
		}
	}
	return dp[len(dp)-1]
}

func sameOp(x, y int) bool {
	if x >= 0 && y >= 0 {
		return true
	}
	if x < 0 && y < 0 {
		return true
	}
	return false
}
