class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vector<int> sub0(n),sub1(n); //差分数组
        sub0[0] = nums[0];
        sub1[0] = target[0];
        for(int i=1;i<n;++i){
            sub0[i] = nums[i]-nums[i-1];
            sub1[i] = target[i]-target[i-1];
        }
        long long pos_ans=0, neg_ans=0;
        for(int i=0;i<n;++i){
            if(sub0[i] < sub1[i]){
                pos_ans += sub1[i] - sub0[i];
            }else{
                neg_ans += sub0[i] - sub1[i];
            }
        }
        return max(pos_ans, neg_ans);
    }
};
