from typing import List
class Solution:
    def call(self,dif):
        prev=0
        ans=0
        for i in range(len(dif)):
            # print(prev,end=' ')
            if dif[i]>=0:
                if prev<dif[i]:
                    ans+=dif[i]-prev
                    prev=dif[i]
                else:
                    prev=dif[i]
            else:
                if prev>dif[i]:
                    if prev<=0:
                        ans+=prev-dif[i]
                        prev=dif[i]
                else:
                    prev=dif[i]
            # print(dif[i],ans)
        return ans
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:      
        dif=[]
        for i,j in zip(nums,target):
            dif.append(j-i)    
        ans=0
        prev=0
        # print(dif)
        diffs=[]
        i=0
        while i<len(dif):
            if dif[i]>=0:
                j=i+1
                while j<len(dif) and dif[j]>=0:
                    j+=1
                diffs.append(dif[i:j])
                i=j
            else:
                j=i+1
                while j<len(dif) and dif[j]<0:
                    j+=1
                diffs.append(dif[i:j])
                i=j
        # print(diffs)
        for i in diffs:
            ans+=self.call(i)
        return ans
