class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ans=0;
        vector<int>diff;
        int n=nums.size();
        for(int i=0;i<n;i++){
            diff.push_back(target[i]-nums[i]);
        }
        int ds=diff.size();
        bool f=false;
        // for(int x:diff) cout<<x<<" ";
        // cout<<endl;
        for(int i=0;i<ds;i++){
            if(diff[i]>=0){
                if(f==false){
                    ans+=diff[i];
                    f=true;
                    if(i+1<ds && diff[i+1]>=diff[i]) ans+=diff[i+1]-diff[i];
                }else{
                    if(i+1<ds && diff[i+1]>=diff[i]){
                         ans+=(diff[i+1]-diff[i]);
                    } 
                }
            }else{
                f=false;
            }
        }
        f=false;
        for(int i=0;i<ds;i++){
            if(diff[i]<=0){
                if(f==false){
                    ans+=abs(diff[i]);
                    f=true;
                    if(i+1<ds && diff[i+1]<=diff[i]) ans+=abs(diff[i+1])-abs(diff[i]);
                }else{
                    if(i+1<ds && diff[i+1]<=diff[i]){
                         ans+=(abs(diff[i+1])-abs(diff[i]));
                    } 
                }
            }else{
                f=false;
            }
        }
        return ans;
    }
};
