class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        diff = [nums[i]- target[i] for i in range(len(nums))]
        dp = [0] * len(diff)
        dp[0] = abs(diff[0])
        increment = diff[0]
        for i in range(1,len(diff)):
            if increment != 0 and increment * diff[i] > 0:
                if abs(diff[i]) >= abs(diff[i-1]):
                    dp[i] = dp[i-1] + abs(diff[i]) - abs(diff[i-1])
                    increment = diff[i]
                else:
                    dp[i] = dp[i-1]
                    increment = diff[i]
            elif increment != 0 and increment * diff[i] < 0:
                dp[i] = dp[i-1] + abs(diff[i])
                increment = diff[i]
            elif increment == 0 or diff[i] == 0:
                dp[i] = dp[i-1] + abs(diff[i])
                increment = diff[i]


        # if (neg == (diff[-2] >= 0)) :
            # print(dp)
        return dp[-1]
