class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        long long ans = 0;
        int pre = 0;
        for(int i=0;i<n;++i){
            int diff = target[i]-nums[i];
            if(pre<diff){
                ans+=diff-pre;
            }
            pre = diff;
        }
        if(pre<0){
            ans+=-pre;
        }
        return ans;
    }
};
