class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        difference = [target[i] - nums[i] for i in range(len(nums))]
        total_ops = 0
        curr_seg_diff = 0

        for i in range(len(difference)):
            if i == 0 or (difference[i - 1] > 0 and difference[i] > 0) or (difference[i - 1] < 0 and difference[i] < 0):
                add_ops = abs(difference[i]) - abs(curr_seg_diff)
                if add_ops > 0:
                    total_ops += add_ops
                curr_seg_diff = difference[i]
            else:
                total_ops += abs(difference[i])
                curr_seg_diff = difference[i]

        return total_ops
