class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ans=0, peak=0, res=0;
        // for(int i=0;i<nums.size();i++){
        //     if(target[i]-nums[i]!=0){peak=target[i]-nums[i];i++;break;}
        // }
        // cout<<peak<<endl;
        for(int i=0;i<nums.size();i++){
            if(peak>0 and target[i]-nums[i]<=0){
                ans+=peak-res;peak=target[i]-nums[i];res=0;
            }
            else if(peak<0 and target[i]-nums[i]>=0){
                ans+=abs(peak-res);peak=target[i]-nums[i];res=0;
            }
            else if(peak>0 and target[i]-nums[i]>0){
                if(peak<target[i]-nums[i])peak=target[i]-nums[i];
                else if(peak>target[i]-nums[i]){ans+=peak-res;peak=target[i]-nums[i];res=target[i]-nums[i];}
            }
            else if(peak<0 and target[i]-nums[i]<0){
                if(peak>target[i]-nums[i])peak=target[i]-nums[i];
                else if(peak<target[i]-nums[i]){ans+=abs(peak-res);peak=target[i]-nums[i];res=target[i]-nums[i];}
            }
            else if(peak==0){
                peak=target[i]-nums[i];
            }
            // cout<<peak<<' '<<res<<' '<<ans<<endl;
        }
        ans+=abs(peak-res);
        return ans;
    }
};
