class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        using LL = long long;
        int n = nums.size();
        std::vector<LL> diff(n + 1, 0), sum(n + 1, 0);
        for(int i = 0; i < n; i++) diff[i + 1] = target[i] - nums[i];

        //3 0 0 -2
        //1 -2 2

        //0 1 -3 4

        LL res1 = 0, res2 = 0;
        for(int i = 1; i <= n; i++) {
            sum[i] = diff[i] - diff[i - 1];
            if(sum[i] > 0) res1 += sum[i];
            else res2 -= sum[i];
        }
        return std::max({res1, res2});
        //
    }
};
