impl Solution {
    pub fn minimum_operations(nums: Vec<i32>, target: Vec<i32>) -> i64 {
        let mut res: i64 = 0;
        let n = nums.len();
        let mut diff = vec![0; n];
        for i in 0..n {
            diff[i] = (nums[i] - target[i]) as i64;
        }
        let mut start = diff[0];
        for i in 1..n {
            if diff[i - 1] * diff[i] <= 0 {
                res += start.abs();
                start = diff[i];
            } else if diff[i - 1].abs() < diff[i].abs() {
                res += diff[i].abs() - diff[i - 1].abs();
            }
        }
        res += start.abs();
        return res;
    }
}
