class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n=nums.size();
        vector<int> diff(n,0);
        for(int i=0;i<n;i++)
        {
            diff[i]=target[i]-nums[i];
        }
        long long ans=0;
        for(int i=0;i<n;i++)
        {
            if(i&&((1LL*diff[i]*diff[i-1])>=0))
            {
                if(abs(diff[i-1])>=abs(diff[i]))continue;
                else ans+=abs(diff[i])-abs(diff[i-1]);
            }
            else{
                ans+=abs(diff[i]);
            }
        }
        return ans;
    }
};
