class Solution {
public:
    using ll = long long;
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vector<int> v(n + 1);
        for (int i = 0; i < n; i++) {
            v[i + 1] = target[i] - nums[i];
        }
        ll add = 0, sub = 0, ans = 0;
        for (int i = 1; i <= n; i++) {
            ll d = v[i] - v[i - 1];
            if (d > 0) {
                // + -
                ll mn = min(add, d);
                add -= mn;
                d -= mn;
                sub += d;
                ans += d;
            } else if (d < 0) {
                d = abs(d);
                ll mn = min(sub, d);
                sub -= mn;
                d -= mn;
                add += d;
                ans += d;
            }
        }
        return ans;
    }
};
