class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ans=0;
        long long i=0;
        long long d=0;
        for(int j=0;j<nums.size();j++){
            if(nums[j]==target[j]){
                i=0;
                d=0;
                 continue;
            }
            if(nums[j]<target[j]){
                d=0;
                int a=target[j]-nums[j];
                if(a>i){
                    a=a-i;
                    i=i+a;
                }else{
                    i=a;
                    a=0;
                }
                ans+=a;
            }
            else{
                i=0;
                int a=nums[j]-target[j];
                if(a>d){
                    a=a-d;
                    d=d+a;
                }else{
                    d=a;
                    a=0;
                }
                ans+=a;
            }
        }
        return ans;

    }
};
